CREATE TABLE Products (
    -- Unique, auto-incrementing primary key
    id              SERIAL PRIMARY KEY,

    -- Common Name (Set as NOT NULL, as every plant must have a common name)
    name            VARCHAR(255) NOT NULL,

    -- Latin/Scientific Name
    scientific_name VARCHAR(255),

    -- Category/Type (e.g., 'Houseplant', 'Fruit')
    plant_type      VARCHAR(100),

    -- Plant description (TEXT is suitable for longer content)
    description     TEXT,

    -- Direct image URL (TEXT is suitable for potentially long URLs)
    image_url       TEXT,

    -- URL to the source/license page
    source_page     TEXT
);

